import React, { useState } from 'react'
import {over} from "stompjs";
import SockJS from "sockjs-client";

var stompClient = null;
const Chatroom = () => {

const [publicChat, setPublicChat ] = useState([]);
const [privateChat, setPrivateChat] = useState(new Map());

const [userData, setUserData] = useState({
    username: "",
    recievername:"",
    connected: false,
    message:""
})

const handleUserName = (event) => {
    const {value} = event.target;
    setUserData({...userData, "username" : value});
}

const registerUser = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
}

const onConnected = () => {
    setUserData({...userData, "connected": true});
    stompClient.subscribe('/chatroom/public', onPublicMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessageReceived);
}

const onPublicMessageReceived = (payload) => {
    let payloadData = JSON.parse(payload.body);
    switch(payloadData.status){
        case "JOIN":
            if(!privateChat.get(payloadData.senderName)){
                privateChat.get(payloadData.senderName).push(payloadData);
                setPrivateChat(new Map(privateChat));
            } 
            break;
        case "MESSAGE":
            publicChat.push(payloadData);
            setPublicChat([...publicChat]);
            break;
    }
}

const onPrivateMessageReceived = (payload) => {
    let payloadData = JSON.parse(payload);
    if(privateChat.get(payloadData.senderName)){
        privateChat.get(payloadData.senderName).push(payloadData);
        setPrivateChat(new Map(privateChat));
    } else {
        let list = [];
        list.push(payloadData);
        privateChat.set(payloadData.senderName, list);
        setPrivateChat(new Map(privateChat));
    }
}

const onError = (err) => {
    console.log(err);
}

  return (
    <div className='container'>
        {userData.connected?
        <div>

        </div>
        :
        <div className='register'>
            <input id="user-name" placeholder='Enter the user name' value={userData.username} onChange={handleUserName} />
            <button type="button" onClick={registerUser} >connect</button>
        </div>}    
    </div>
  )
}

export default Chatroom
